// src/firebase.js
// Configuraci贸n e inicializaci贸n de Firebase (ya incluye Auth y Firestore)
import { initializeApp } from "firebase/app";
import { getAuth } from "firebase/auth";
import { getFirestore } from "firebase/firestore";

const firebaseConfig = {
  apiKey: "AIzaSyDMO41dBZm5UF8fldXfLhu_WSrE0TZAKi8",
  authDomain: "latoallaapp-daf6c.firebaseapp.com",
  projectId: "latoallaapp-daf6c",
  storageBucket: "latoallaapp-daf6c.firebasestorage.app",
  messagingSenderId: "403031848271",
  appId: "1:403031848271:web:9e88b4b9006588b6f2c13a"
};

// Inicializa Firebase
const app = initializeApp(firebaseConfig);

// Exporta auth y db para usarlos en la app
export const auth = getAuth(app);
export const db = getFirestore(app);

export default app;
