import React, { useState } from "react";
import { signInWithEmailAndPassword } from "firebase/auth";
import { auth } from "../firebase";
import RegisterForm from "./RegisterForm";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showAccessInput, setShowAccessInput] = useState(false);
  const [accessCode, setAccessCode] = useState("");
  const [showRegisterForm, setShowRegisterForm] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [info, setInfo] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");
    setInfo("");
    setLoading(true);
    try {
      await signInWithEmailAndPassword(auth, email, password);
      setInfo("Login correcto. Bienvenido.");
      // Aquí puedes redirigir a una ruta privada o cambiar estado global
    } catch (err) {
      setError(err.message || "Error al iniciar sesión");
    } finally {
      setLoading(false);
    }
  };

  const handleShowRegister = () => {
    setShowAccessInput(true);
    setShowRegisterForm(false);
    setAccessCode("");
    setError("");
    setInfo("");
  };

  const checkAccessCode = (e) => {
    e.preventDefault();
    setError("");
    setInfo("");
    if (accessCode === "0000") {
      setShowRegisterForm(true);
      setShowAccessInput(false);
    } else {
      setError("Código de acceso incorrecto");
    }
  };

  return (
    <div className="login-wrapper">
      <header className="topbar">
        <div className="logo">Toalla App</div>
        <div>
          <button className="btn small" onClick={handleShowRegister}>Registrarse</button>
        </div>
      </header>

      <main className="card">
        <h2>Iniciar sesión</h2>
        <form onSubmit={handleLogin} className="form">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            autoComplete="email"
          />
          <input
            type="password"
            placeholder="Contraseña"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            autoComplete="current-password"
          />
          <div className="actions">
            <button className="btn" type="submit" disabled={loading}>
              {loading ? "Entrando..." : "Entrar"}
            </button>
            <button
              type="button"
              className="btn outline"
              onClick={() => setShowAccessInput((s) => !s)}
            >
              Registrarse
            </button>
          </div>
        </form>

        {showAccessInput && (
          <form onSubmit={checkAccessCode} className="access-form">
            <input
              type="password"
              placeholder="Introduce la contraseña de registro"
              value={accessCode}
              onChange={(e) => setAccessCode(e.target.value)}
              required
            />
            <button className="btn small" type="submit">Comprobar</button>
          </form>
        )}

        {showRegisterForm && (
          <div className="register-area">
            <h3>Formulario de registro</h3>
            <RegisterForm onRegistered={() => setShowRegisterForm(false)} />
          </div>
        )}

        {error && <p className="error">{error}</p>}
        {info && <p className="info">{info}</p>}
      </main>
    </div>
  );
}
